import React from "react";
import Toolbar from "../Toolbar/Toolbar";
import SideDrawer from "../Sidedrawer/Sidedrawer";
import HeaderIntro1 from "./HeaderIntro/HeaderIntro1/HeaderIntro1";
import HeaderIntro2 from "./HeaderIntro/HeaderIntro2/HeaderIntro2";
import BannerMain from "./Banners/BannerMain/BannerMain";
import ServiceMain from "./Service/ServiceMain/ServiceMain";
import WhyChooseUsMain from "./whyChooseUs/whyChooseUsMain/whyChooseUsMain";
import FooterMain from "../Footer/FooterMain/FooterMain";
import { Component } from "react/cjs/react.production.min";

class HomePage extends Component {

    state = {

        backDropValue: false,
        sideBar: false

      }

    toggleBackdrop= () => {
    const current = this.state.backDropValue;
    this.setState({backDropValue: !current, sideBar: false});
    }

    toggleSidebar = () => {
    this.setState({sideBar:true, backDropValue:true});
    console.log(this.state.sideBar)
    }


    render () {

        return (
            <div>
                <Toolbar switchBar={this.toggleSidebar}/>
                <SideDrawer backdrop={this.state.backDropValue} 
                            toggleBackdrop={this.toggleBackdrop}
                            switch={this.state.sideBar} />
                <HeaderIntro1 />
                <BannerMain />
                <HeaderIntro2 />
                <ServiceMain />
                <WhyChooseUsMain />
                <FooterMain />
            </div>
        )
    }
}

export default HomePage;
