import React, { Component } from 'react';
import { Route, Routes } from 'react-router-dom';
// import desktop from '../../../../../Assets/Images/pcAccesories/desktop.jpg';
import desktop from '../../../../../Assets/Images/pcAccesories/desktop.jpg';
import pcItem from '../../PcItem/PcItem';



//manage state here

class PcCategory extends Component {

    state = {

        category: {
            desktop: 'desktop',
            keyboard: 'keyboard',
            laptop: 'laptop',
            motherboard: 'motherboard'
        }
    }

    componentDidMount() {
        let x = Object.keys(this.state.category).map(item => {
            let dir = `/${item}`;
            console.log(dir);
            return <Routes>
                <Route path={dir} element={<pcItem category={item}/>}/>
            </Routes>
        })
    }


    render () {



        return (

            <div>
                <h2>Shop By Category</h2>
                <div>
                    {x}
                </div>
            </div>

        )
    }
}

export default PcCategory;
