import React, { Component } from 'react';
import axios from 'axios';
import Toolbar from '../Toolbar/Toolbar'
import SideDrawer from '../Sidedrawer/Sidedrawer';
import FooterMain from '../Footer/FooterMain/FooterMain'
import bgMain from '../../Assets/Images/transfer.jpg';
import Aux from '../../Hoc/Aux/Aux'
import styles from './CurrencyConverter.module.css';

class CurrencyConverter extends Component {

    state = {
        rate: null
    }

    componentDidMount () {
        axios.get('http://api.exchangeratesapi.io/v1/latest?access_key=dda523c1f826a48326075c1671b187d3&format=1')
        .then(res => {
            this.setState({rates: res.data});
        })
    }

    render () {

        let option = null;

        Object.keys(this.state.rate['rates']).map(item => {
            return option = <option value={item}>{item}</option>;
        })
        console.log(this.state.rate)

        return (
            <Aux>
                <Toolbar switchBar={this.props.switchBar}/>
                <SideDrawer backdrop={this.props.backdrop} 
                            toggleBackdrop={this.props.toggleBackdrop}
                            switch={this.props.switch} />
                <div className={styles.Main}>
                    <div className={styles.Details}>
                        <h1>Send Money From The UK</h1>
                        <h3>Send money to all over the world via trusted company from our shop</h3>
                        <div className={styles.SubDetails}>
                            <select name="from">
                                {option}
                            </select>

                            <select name="to">
                                <option value="UK">UK</option>
                            </select>
                        </div>                
                    </div>

                    <img src={bgMain} />

                </div>
                <FooterMain />
            </Aux>
        )

    }

}

export default CurrencyConverter;
