import React from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import { Component } from 'react/cjs/react.production.min';
import './App.css';
import HomePage from './Pages/Homepage/Homepage';
import Phone from './Pages/Repair/Phone/Phone';


class App extends Component {

  state = {

    backDropValue: false,
    sideBar: false

  }

  toggleBackdrop= () => {
  const current = this.state.backDropValue;
  this.setState({backDropValue: !current, sideBar: false});
  }

  toggleSidebar = () => {
  this.setState({sideBar:true, backDropValue:true});
  }


  render () {

    return (
      <div className='App'>
        <BrowserRouter>
          <Routes>
            <Route path="/" exact element={<HomePage switchBar={this.toggleSidebar} 
                                                     backdrop={this.state.backDropValue}
                                                     toggleBackdrop={this.toggleBackdrop}
                                                     switch={this.state.sideBar}/>} />

            <Route path="/phone/*" element={<Phone switchBar={this.toggleSidebar} 
                                                  backdrop={this.state.backDropValue}
                                                  toggleBackdrop={this.toggleBackdrop}
                                                  switch={this.state.sideBar}/>} />
          </Routes>
        </BrowserRouter>
      </div>
    )
  }
}


export default App;
