import React from 'react'
import Aux from '../../../Hoc/Aux/Aux';
import emailjs from '@emailjs/browser';
import styles from './InquiryForm.module.css';


const inquiryForm = (props) => {

    function sendInquiry (event) {
        event.preventDefault();

        emailjs.sendForm('gmail', 'template_gyuauft', form.current, 'user_cEwV4WZvDeX8VQnYqIdlW')
            .then((result) => {
                console.log(result.text);
            }, (error) => {
                console.log(error.text);
            });
        event.target.reset();
    };




    return (
        <Aux>
            <h1>{props.device}</h1>
            <img src={props.img}/>
            <form onSubmit={sendInquiry}>
                <div className={styles.Form}>
                    <input type="text" placeholder='Make' name='make'/>
                    <input type="text" placeholder='Model' name='model'/>
                    <textarea name='problem' rows={7} cols={40} placeholder='Whats the Problem' />
                    <select name='serviceType'>
                        <option value="Normal">Normal</option>
                        <option value="Urgent">Urgent</option>
                    </select>
                    <textarea name='message' rows={7} cols={40} placeholder='Additional Information' />
                    <input type="text" placeholder='Your Name' name='name'/>
                    <input type="text" placeholder='Phone Number' name='contact'/>
                </div>

                <button>Send Inquiry</button>

            </form>

        </Aux>
    )
}

export default inquiryForm;
