import React from 'react';
import { Link } from 'react-router-dom';
import aux from '../../../Hoc/Aux/Aux';
import styles from './NavigationItem.module.css';


const navigationItem = (props) => {
    let nestedLink = null;


    if (props.child === 'repairs'){
        nestedLink = <li className={styles.NavigationItem} onMouseOver={props.expadnNav} >
            <ul className={styles.nestedLink}>
                <Link to="/repair/phone">
                    <p>Phone</p>
                </Link>

                <Link to="/repair/tablet">
                    <p>Tablet</p>
                </Link>

                <Link to="/repair/laptop">
                    <p>Laptop</p>
                </Link>
            </ul>
            <a href={props.link} className={props.active? styles.active : null}>{props.children}</a>
        </li>
    }
    else if (props.child === 'sale'){
        nestedLink = <li className={styles.NavigationItem}>
            <ul className={styles.nestedLink}>
                <Link to="/repair/phone">
                    <p>Phone</p>
                </Link>

                <Link to="/repair/tablet">
                    <p>Tablet</p>
                </Link>

                <Link to="/repair/laptop">
                    <p>Laptop</p>
                </Link>
            </ul>
            <a href={props.link} className={props.active? styles.active : null}>{props.children}</a>
        </li>
    }
    else {
        nestedLink = <li className={styles.NavigationItem}>
            <a href={props.link} className={props.active ? styles.active : null}>{props.children}</a>
        </li>
    }

    return (
        nestedLink
    )
}

export default navigationItem;
