import { Component } from 'react';
import './App.css';
import BannerMain from './Pages/Homepage/Banners/BannerMain/BannerMain';
import HeaderIntro1 from './Pages/Homepage/HeaderIntro/HeaderIntro1/HeaderIntro1';
import HeaderIntro2 from './Pages/Homepage/HeaderIntro/HeaderIntro2/HeaderIntro2';
import SideDrawer from './Pages/Sidedrawer/Sidedrawer';
import Toolbar from './Pages/Toolbar/Toolbar';
import ServiceMain from './Pages/Homepage/Service/ServiceMain/ServiceMain';
import WhyChooseUsMain from './Pages/Homepage/whyChooseUs/whyChooseUsMain/whyChooseUsMain';
import FooterMain from './Pages/Footer/FooterMain/FooterMain';
import HomePage from './Pages/Homepage/Homepage';
import homePage from './Pages/Homepage/Homepage';

class App extends Component {

  state = {

    backDropValue: false,
    sideBar: false

  }

  toggleBackdrop= () => {
    const current = this.state.backDropValue;
    this.setState({backDropValue: !current, sideBar: false});
  }

  toggleSidebar = () => {
    this.setState({sideBar:true, backDropValue:true});
    console.log(this.state.sideBar)
  }

  render () {

    return (
      <div className="App">
        <HomePage switchbar={this.toggleSidebar}
                  backdrop={this.state.backDropValue} 
                  toggleBackdrop={this.toggleBackdrop}
                  switch={this.state.sideBar} />
      </div>
    );

  }
}

export default App;
