import React from 'react';
import styles from './PcItems.module.css';

const pcItems = (props) => {

    const list = props.details[props.items].map(item => {
        return <li>{item}</li>
    })

    return (
        <div className={styles.Main}>
            <img src={props.img}/>
            <div>
                <h3>{props.items}</h3>
                <ul>
                    {list}
                </ul>
            </div>
        </div>
    )
}

export default pcItems;
