import { Component } from 'react';
import './App.css';
import BannerMain from './Components/Banners/BannerMain/BannerMain';
import HeaderIntro1 from './Components/HeaderIntro/HeaderIntro1/HeaderIntro1';
import HeaderIntro2 from './Components/HeaderIntro/HeaderIntro2/HeaderIntro2';
import SideDrawer from './Components/Sidedrawer/Sidedrawer';
import Toolbar from './Components/Toolbar/Toolbar';
import ServiceMain from './Components/Service/ServiceMain/ServiceMain';
import WhyChooseUsMain from './Components/whyChooseUs/whyChooseUsMain/whyChooseUsMain';
import FooterMain from './Components/Footer/FooterMain/FooterMain';

class App extends Component {

  state = {

    backDropValue: false,
    sideBar: false

  }

  toggleBackdrop= () => {
    const current = this.state.backDropValue;
    this.setState({backDropValue: !current, sideBar: false});
  }

  toggleSidebar = () => {
    this.setState({sideBar:true, backDropValue:true});
    console.log(this.state.sideBar)
  }

  render () {

    return (
      <div className="App">
        <Toolbar switchBar={this.toggleSidebar}/>
        <SideDrawer backdrop={this.state.backDropValue} 
                    toggleBackdrop={this.toggleBackdrop}
                    switch={this.state.sideBar}/>
        <HeaderIntro1 />
        <BannerMain />
        <HeaderIntro2 />
        <ServiceMain />
        <WhyChooseUsMain />
        <FooterMain />
      </div>
    );

  }
}

export default App;
