import React from 'react';
import styles from './PcItems.module.css';

const pcItems = (props) => {

    const list = props.details.map(item => {
        return <ul>
            <li>{item}</li>
        </ul>
    })

    return (
        <div className={styles.Main}>
            <img src={props.img}/>
            <div>
                <h3>{props.item}</h3>
                {list}
            </div>
        </div>
    )
}

export default pcItems;
