import sp8 from '../../../../Assets/Images/speaker/sp8.jpg';
import { isEditable } from "@testing-library/user-event/dist/utils";








class ItemDetails extends Component {

    state = {

        keyboard: {
            k1: {
                name: 'k1',
                price: '£20',
                available: true,
                img: k1,
                details: 'Gaming Keyboard RGB USB Wired Rainbow Keyboard'
            },
            k2: {
                name: 'k2',
                price: '£20',
                available: true,
                img: k2,
                details: 'Amazon Basics Matte Black Wired Keyboard, UK QWERTY Layout'
            },
            k3: {
                name: 'k3',
                price: '£20',
                available: true,
                img: k3,
                details: 'Bactlit Gaming Keyboard,Rii RK100 Plus 7 Color Rainbow LED Backlit Mechanical Keyboard'
            },
            k4: {
                name: 'k4',
                price: '£20',
                available: true,
                img: k4,
                details: 'Microsoft Wireless Desktop 900 Keyboard and Mouse - Black'
            },
            k5: {
                name: 'k5',
                price: '£20',
                available: true,
                img: k5,
                details: 'Microsoft Wireless Desktop 850 Keyboard'
            },
            k6: {
                name: 'k6',
                price: '£20',
                available: true,
                img: k6,
                details: 'Microsoft Wireless Desktop 900 Keyboard and Mouse - Black'
            },
            k7: {
                name: 'k7',
                price: '£20',
                available: true,
                img: k7,
                details: 'Amazon Basics Matte Black Wired Keyboard, UK QWERTY Layout'
            },
            k8: {
                name: 'k8',
                price: '£20',
                available: true,
                img: k8,
                details: 'Microsoft Wireless Desktop 850 Keyboard'
            },
        },

        laptop: [l1, l2, l3, l4, l5, l6, l7, l8],
        monitor: [s1, s2, s3, s4, s5, s6, s7, s8],
        mouse: [m1, m2, m3, m4, m5, m6, m7, m8],
        power: [],
        ram: [r1, r2, r3, r4, r5, r6, r7, r8],
        speaker: [sp1, sp2, sp3, sp4, sp5, sp6, sp7, sp8],
        motherboard: []

    }

    componentDidMount () {
        window.scrollTo(0, 0);
    }

    render () {


        let product = this.state[this.props.device];
        console.log(Object.values(product['k1']));

        let item = Object.keys(product).map(item => {
            return <div key={item} className={styles.Items}>
                <img src={product[item]['img']} />
                <h3>{product[item]['details']}</h3>
                <h3>{product[item]['price']}</h3>
            </div>
        })



        // if (this.state[this.props.device].length === 0) {
        //     item = <h3 className={styles.Empty}>Sorry No Item</h3>
        // }



        return (
            <Aux>
                <Toolbar switchBar={this.props.switchBar}/>

                <SideDrawer backdrop={this.props.backdrop} 
                            toggleBackdrop={this.props.toggleBackdrop}
                            switch={this.props.switch} />

                <div className={styles.Main}>
                    {item}
                </div>

                <FooterMain />

            </Aux>

        )
    }
}

export default ItemDetails;
