import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import styles from './NavigationItem.module.css';
import BaseContext from '../../../Hoc/Authcontext/BaseContext';


class NavigationItem extends Component {

    static contextType = BaseContext;

    // detectWidthChange = (event) => {
    //     event.onchange = () => {

    //     }
    // }

    render () {
        let nestedLink = null;

        let classes = '';

        if (this.context.state.nestedNavValue1 === true) {
            classes = 'nestedLinkOn';
        }
        else {
            classes = 'nestedLink';
        }

        if (this.props.child === 'repairs'){
            nestedLink = <li className={styles.NavigationItem} onClick ={() => this.context.expandNav('item1')}>
                <ul className={styles.classes} >
                    <Link to="/repair/phone" onClick={this.context.closeNav}>
                        <p>Phone</p>
                    </Link>

                    <Link to="/repair/tablet" onClick={this.context.closeNav}>
                        <p>Tablet</p>
                    </Link>

                    <Link to="/repair/laptop" onClick={this.context.closeNav}>
                        <p>Laptop</p>
                    </Link>
                </ul>
                <a href={this.props.link} className={this.props.active? styles.active : null}>{this.props.children}</a>
            </li>
        }
        else if (this.props.child === 'sale'){
            nestedLink = <li className={styles.NavigationItem} onClick ={() => this.context.expandNav('item2')}>
                <ul style={this.context.state.nestedNavValue2 ? {display: 'flex'}: {display: 'none'}} 
                    className={styles.nestedLink} >
                    <Link to="/repair/phone">
                        <p>Phone</p>
                    </Link>

                    <Link to="/repair/tablet">
                        <p>Tablet</p>
                    </Link>

                    <Link to="/repair/laptop">
                        <p>Laptop</p>
                    </Link>
                </ul>
                <a href={this.props.link} className={this.props.active? styles.active : null} >{this.props.children}</a>
            </li>
        }
        else {
            nestedLink = <li className={styles.NavigationItem}>
                <a href={this.props.link} className={this.props.active ? styles.active : null}>{this.props.children}</a>
            </li>
        }


        return (
            nestedLink
        )

    }
}

export default NavigationItem;
