import React from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import { Component } from 'react/cjs/react.production.min';
import './App.css';
import HomePage from './Pages/Homepage/Homepage';
import Phone from './Pages/Repair/Phone/Phone';
import PhoneModels from './Pages/Repair/Phone/PhoneModels/PhoneModels';
import RepairQuote from './Pages/Repair/Phone/RepairQuote/RepairQuote';


class App extends Component {

  state = {

    backDropValue: false,
    sideBar: false,

    model: ['apple', 'asus', 'blackberry', 'htc', 'huawei', 'lg', 'nexus', 'nokia', 'oneplus',
                'pixel', 'samsung', 'sony']

  }

  toggleBackdrop= () => {
  const current = this.state.backDropValue;
  this.setState({backDropValue: !current, sideBar: false});
  }

  toggleSidebar = () => {
  this.setState({sideBar:true, backDropValue:true});
  }


  render () {

    return (
      <BrowserRouter>
        <div className='App'>
            <Routes>
              <Route path="/" exact element={<HomePage switchBar={this.toggleSidebar} 
                                                       backdrop={this.state.backDropValue}
                                                       toggleBackdrop={this.toggleBackdrop}
                                                       switch={this.state.sideBar}/>} />

              <Route path="phone//*" element={<Phone switchBar={this.toggleSidebar} 
                                                     backdrop={this.state.backDropValue}
                                                     toggleBackdrop={this.toggleBackdrop}
                                                     switch={this.state.sideBar}/>} />

              {this.state.model.map(item => {
                let dir = `/phone/${item}` ;

                return <Route key={item} path={dir} element={<PhoneModels model={item} 
                                                    switchBar={this.toggleSidebar} 
                                                    backdrop={this.state.backDropValue}
                                                    toggleBackdrop={this.toggleBackdrop}
                                                    switch={this.state.sideBar}/>} 
                                                    />

              })}

              <Route path="/phone/devices/repair" element={<RepairQuote switchBar={this.toggleSidebar} 
                                                    backdrop={this.state.backDropValue}
                                                    toggleBackdrop={this.toggleBackdrop}
                                                    switch={this.state.sideBar}/>} />





            </Routes>
        </div>
      </BrowserRouter>
    )
  }
}


export default App;
