import React from 'react';
import { Link } from 'react-router-dom';
import { Component } from 'react/cjs/react.production.min';
import styles from './NavigationItem.module.css';


class NavigationItem extends Component {

    state = {
        nestedValue: false
    }

    switchNav = () => {
        this.setState({nestedValue: true});
        // document.body.addEventListener('click', () => {
        //     this.setState({nestedValue: false})
        // })
    }



    render () {


        let nestedLink = null;

        if (this.props.child === 'repairs'){
            nestedLink = <li className={styles.NavigationItem} onClick={this.switchNav}>
                <ul className={styles.nestedLink} style={this.state.nestedValue ? {display: 'flex'}: {display: 'none'}}>
                    <Link to="/repair/phone">
                        <p>Phone</p>
                    </Link>

                    <Link to="/repair/tablet">
                        <p>Tablet</p>
                    </Link>

                    <Link to="/repair/laptop">
                        <p>Laptop</p>
                    </Link>
                </ul>
                <a href={this.props.link} className={this.props.active? styles.active : null}>{this.props.children}</a>
            </li>
        }
        else if (this.props.child === 'sale'){
            nestedLink = <li className={styles.NavigationItem}>
                <ul className={styles.nestedLink}>
                    <Link to="/repair/phone">
                        <p>Phone</p>
                    </Link>

                    <Link to="/repair/tablet">
                        <p>Tablet</p>
                    </Link>

                    <Link to="/repair/laptop">
                        <p>Laptop</p>
                    </Link>
                </ul>
                <a href={this.props.link} className={this.props.active? styles.active : null} >{this.props.children}</a>
            </li>
        }
        else {
            nestedLink = <li className={styles.NavigationItem}>
                <a href={this.props.link} className={this.props.active ? styles.active : null}>{this.props.children}</a>
            </li>
        }


        return (
            nestedLink
        )

    }
}

export default NavigationItem;
