import React, { useEffect } from "react";
import Aux from '../../../Hoc/Aux/Aux';
import Toolbar from "../../Toolbar/Toolbar";
import SideDrawer from "../../Sidedrawer/Sidedrawer";
import FooterMain from "../../Footer/FooterMain/FooterMain";
import bgPhone from '../../../Assets/Images/phoneMain2.jpg';
import aos from 'aos';
import 'aos/dist/aos.css';
import styles from './PhoneMain.module.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTools, faMobile } from '@fortawesome/free-solid-svg-icons';
import { Link } from "react-router-dom";

const PhoneMain = (props) => {

    useEffect(() => {
        window.scrollTo(0, 0);

        aos.init({duration: 1500});
    }, [])
    return (
        <Aux>
            <Toolbar switchBar={props.switchBar}/>

            <SideDrawer backdrop={props.backdrop} 
                        toggleBackdrop={props.toggleBackdrop}
                        switch={props.switch} />

            <div className={styles.Main}>
                <div className={styles.Heading}>
                    <h1>PHONE CLINIC</h1>
                    <div>
                        <Link to="/phone/phonerepair">
                            <h3>Take me to Repair</h3>
                        </Link>
                        <h2>Or Buy Phones</h2>
                    </div>
                </div>
                <div data-aos="fade-left" data-aos-easing="ease-out-cubic" className={styles.Img}>
                    <img src={bgPhone} />
                </div>
            </div>

            <div className={styles.Menu}>
                <h1>What you looking for today</h1>

                <div className={styles.SubMenu}>
                    <Link to="/phone/phonerepair">
                        <div>
                            <FontAwesomeIcon icon={faTools} size="3x"/>
                            <h2>Phone Repair</h2>
                        </div>
                    </Link>

                    <Link to="/phone/shopphone">
                        <div>
                            <FontAwesomeIcon icon={faMobile} size="3x"/>
                            <h2>Phone Sale</h2>
                        </div>
                    </Link>
                </div>

            </div>

            <FooterMain />
        </Aux>
    )
}

export default PhoneMain;
