import React from 'react';
import { Link } from 'react-router-dom';
import { Component } from 'react/cjs/react.production.min';
import aux from '../../../Hoc/Aux/Aux';
import styles from './NavigationItem.module.css';


class NavigationItem extends Component {

    state = {

        nestedLinkValue: false
    }


    expandNav = () => {
        const nestedValue = this.state.nestedLinkValue;
        nestedLinkValue = !nestedValue
    }


    render () {
        let nestedLink = null;


        if (props.child === 'repairs'){
            nestedLink = <li className={styles.NavigationItem} onMouseOver={this.expandNav} >
                <ul style={this.state.nestedLinkValue ? {display: 'flex'} : {display: 'none'}} className={styles.nestedLink}>
                    <Link to="/repair/phone">
                        <p>Phone</p>
                    </Link>

                    <Link to="/repair/tablet">
                        <p>Tablet</p>
                    </Link>

                    <Link to="/repair/laptop">
                        <p>Laptop</p>
                    </Link>
                </ul>
                <a href={props.link} className={this.props.active? styles.active : null}>{this.props.children}</a>
            </li>
        }
        else if (this.props.child === 'sale'){
            nestedLink = <li className={styles.NavigationItem}>
                <ul className={styles.nestedLink}>
                    <Link to="/repair/phone">
                        <p>Phone</p>
                    </Link>

                    <Link to="/repair/tablet">
                        <p>Tablet</p>
                    </Link>

                    <Link to="/repair/laptop">
                        <p>Laptop</p>
                    </Link>
                </ul>
                <a href={this.props.link} className={this.props.active? styles.active : null}>{this.props.children}</a>
            </li>
        }
        else {
            nestedLink = <li className={styles.NavigationItem}>
                <a href={this.props.link} className={this.props.active ? styles.active : null}>{this.props.children}</a>
            </li>
        }

        return (
            nestedLink
        )

    }
}

export default NavigationItem;
