import React, { useContext, useEffect } from "react";
import Logo from "../Logo/Logo";
import NavigationItems from '../Navigation/NavigationItems/NavigationItems'
import styles from './Sidedrawer.module.css';
import Aux from "../../Hoc/Aux/Aux";
import { useLocation } from "react-router-dom";
import Backdrop from '../Backdrop/Backdrop'
import BaseContext from "../../Hoc/Authcontext/BaseContext";
import { createRef } from "react/cjs/react.production.min";


const SideDrawer = (props) => {

    const context = useContext(BaseContext);


    const dir = useLocation()


    useEffect(() => {
        console.log(dir.pathname)
        context.closeNav();
        console.log(props.switch)
    }, [props.switch])

    return (
        <Aux>
            <Backdrop trigger={props.backdrop} click={props.toggleBackdrop}/>
            <div className={props.switch ? styles.Sidedrawer : styles.Off} onClick={context.closeNav}>
                <div className={styles.Logo}>
                    <Logo />
                </div>
                <nav className={styles.Nav}>
                    <NavigationItems />
                </nav>
            </div>
        </Aux>
    )
}

export default SideDrawer;
