import React, { useEffect } from 'react'
import Aux from '../../../Hoc/Aux/Aux';
import emailjs from '@emailjs/browser';
import styles from './InquiryForm.module.css';
import Toolbar from '../../Toolbar/Toolbar';
import SideDrawer from '../../Sidedrawer/Sidedrawer';
import FooterMain from '../../Footer/FooterMain/FooterMain';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFileSignature, faPhoneAlt, faStore } from '@fortawesome/free-solid-svg-icons';


const InquiryForm = (props) => {

    useEffect(() => {
        window.scrollTo(0, 0);
    })

    function sendInquiry (event) {
        event.preventDefault();

        emailjs.sendForm('service_zla7qih', 'template_gyuauft', event.target, 'user_cEwV4WZvDeX8VQnYqIdlW')
            .then((result) => {
                console.log(result.text);
            }, (error) => {
                console.log(error.text);
            });
        event.target.reset();
    };




    return (
        <Aux>
            <Toolbar />
            <SideDrawer />
            <div className={styles.Img}> 
                <img src={props.img}/>
            </div>

            <div className={styles.Heading}>

                <h1>How the Repair Process Works</h1>

                <div className={styles.SubHeading}>
                    <div className={styles.Subs}>
                        <FontAwesomeIcon icon={faFileSignature}/>
                        <h3>Fill up the form below including Phone Make, Model, Problems and Contact Details</h3>
                    </div>
                    <div className={styles.Subs}>
                        <FontAwesomeIcon icon={faPhoneAlt}/>
                        <h3>You will receive a phone call or email about your appoinment time and price</h3>
                    </div>
                    <div className={styles.Subs}>
                        <FontAwesomeIcon icon={faStore}/>
                        <h3>Come in to our shop and have your fixed</h3>
                    </div>
                </div>
            </div>


            <div className={styles.FormMain}>
                <form onSubmit={sendInquiry} className={styles.Form}>
                    <div className={styles.Phone}>
                        <input readOnly name='serviceName' value={props.device} className={styles.Hidden} />
                        <input type="text" placeholder='Make' name='make'/>
                        <input type="text" placeholder='Model' name='model'/>
                    </div>

                    <textarea name='problem' rows={7} cols={30} placeholder='Whats the Problem' />

                    <select name='serviceType'>
                        <option disabled={true} selected="Select Service Type">Select Service Type</option>
                        <option value="Normal">Normal</option>
                        <option value="Urgent">Urgent</option>
                    </select>

                    <textarea name='message' rows={7} cols={30} placeholder='Additional Information' />

                    <div className={styles.Phone}>
                        <input type="text" placeholder='Your Name' name='name'/>
                        <input type="text" placeholder='Phone Number' name='contact'/>
                    </div>

                    <button type='submit' >Send Inquiry</button>

                </form>
                <div className={styles.formPic}>
                    <div className={styles.Subform}>
                        <h1>Phone Clinic</h1>
                        <h2>Fill up the form using your information and we will get back t you within an hour</h2>
                    </div>

                </div>
            </div>

            <FooterMain />

        </Aux>
    )
}

export default InquiryForm;
