import React, { useEffect } from "react";
import styles from './HeaderIntro2.module.css';
import img from '../../../Assets/Images/phoneClinic.jpg';
import aos from 'aos';
import 'aos/dist/aos.css';

const HeaderIntro2 = () => {

    useEffect(() => {
        aos.init({duration:1900})
    }, [])

    return (
        <div className={styles.HeaderIntro2}>
            <div data-aos="fade-right">
                <img className={styles.Img} src={img}/>
            </div>

            <div data-aos="fade-left" className={styles.Para}>
                <h2>We Provide The Best Services To Repair Your Gagdet</h2>
                <p>While You Wait was established early in the year 2004. We have been repairing and fixing mobile phones
                    ever since then. We understand the urgency you may feel, if and when, your mobile phone breaks,
                     which is why we brought out the concept of While You Wait Repairs. We must highlight that we 
                     have an extensively organised workshop, where our technicians work. We also keep a large 
                     stock of high quality spares, which will not only be compatible with your mobile device,
                      but will also be calibrated to give you the look and feel of the original handset. 
                      Our promptness is evident in our work. If you are looking for a suitable quote for a repair
                       job, please feel free to get in touch in the section at the bottom of your screen.
                </p>
                <ul>
                    <li>Quick Repair Process</li>
                    <li>Same Day Repair</li>
                    <li>Low Price Garuntee</li>
                    <li>Free Diagnostics</li>
                </ul>
            </div>

        </div>

    )
}

export default HeaderIntro2;
