import React from 'react';
import { Link } from 'react-router-dom';
import { Component } from 'react/cjs/react.production.min';
import styles from './NavigationItem.module.css';


class NavigationItem extends Component {

    render () {
        let nestedLink = null;



        if (this.props.child === 'repairs'){
            nestedLink = <li className={styles.NavigationItem}>
                <ul style={this.props.switchNavs ? {display: 'flex'} : {display: 'none'}} className={styles.nestedLink}>
                    <Link to="/repair/phone">
                        <p>Phone</p>
                    </Link>

                    <Link to="/repair/tablet">
                        <p>Tablet</p>
                    </Link>

                    <Link to="/repair/laptop">
                        <p>Laptop</p>
                    </Link>
                </ul>
                <a href={this.props.link} className={this.props.active? styles.active : null} onMouseOver={this.props.expandNav} onMouseLeave={this.props.returnNav}>{this.props.children}</a>
            </li>
        }
        else if (this.props.child === 'sale'){
            nestedLink = <li className={styles.NavigationItem} onMouseOver={this.props.expandNav} onMouseLeave={this.props.returnNav}>
                <ul style={this.props.swithcNavs ? {display: 'flex'} : {display: 'none'}} className={styles.nestedLink}>
                    <Link to="/repair/phone">
                        <p>Phone</p>
                    </Link>

                    <Link to="/repair/tablet">
                        <p>Tablet</p>
                    </Link>

                    <Link to="/repair/laptop">
                        <p>Laptop</p>
                    </Link>
                </ul>
                <a href={this.props.link} className={this.props.active? styles.active : null} >{this.props.children}</a>
            </li>
        }
        else {
            nestedLink = <li className={styles.NavigationItem}>
                <a href={this.props.link} className={this.props.active ? styles.active : null}>{this.props.children}</a>
            </li>
        }

        console.log(this.props.switchNavs)

        return (
            nestedLink
        )

    }
}

export default NavigationItem;
